import emailjs from '@emailjs/browser';

import './App.css'
import { json } from 'express';


function EmailOTP () {

	function handleSubmitEmail(e){
		e.preventDefault();
		const email = document.getElementById('email');
		console.log(email.value);

		const otpNumber = Math.floor(Math.random()*600000);
		console.log(otpNumber);

		var Params = {
			mail_to: email,
			otp: otpNumber
		};

		emailjs.send('service_7mfhixv', 'template_i6cppcw', Params, 'btVGQG6XQEU4PFvoX')
			.then(function(response) {
			   console.log('SUCCESS!', response.status, response.text);
			}, function(error) {
			   console.log('FAILED...', error);
			}
		);
	}

	return(
		<div className='EmailOTP body'>

			<h2>Verify Your Email 2</h2>

			<h4>Enter E-Mail</h4>
			<form onSubmit={handleSubmitEmail}>
				<input type="email" name='email' id='email' placeholder="Enter your Email" required />
				<button type='submit' name='sign-in-button' id='sign-in-button' value='Get Code'>nhh</button>
			</form>

			{/* <h4>Enter OTP</h4>
