import React, { useCallback } from 'react';
import { useStytch } from 'stytch';

import './App.css'


class EmailOTP extends React.Component {

	handleChange = (e) => {
		const { name, value } = e.target
		this.setState({
			[name]: value
		})
	}

	Login = (e) => {
		e.preventDefault()
		const email = this.state.email
		const stytchClient = useStytch();
		const sendPasscode = useCallback(() => {
			stytchClient.otps.email.send('jerinmr51@gmail.com', {
			expiration_minutes: 5,
			});
		}, [stytchClient]);
		return <button onClick={sendPasscode}>Send passcode</button>;
	};

	render() {
		return(
			<div className='app'>

				<h2>Verify Your Email</h2>

				<h4>Enter E-Mail</h4>
				<form onSubmit={this.Login}>
					<input type="email" name='email' id='email' placeholder="Enter your Email" required  onChange={this.handleChange}/>
					<input type='submit' name='sign-in-button' id='sign-in-button' value='Get Code'/>
				</form>

				<h4>Enter OTP</h4>
				<form onSubmit={this.onSubmitOTP}>
					<input type="number" name='otp' id='otp' placeholder="Enter your OTP number" required onChange={this.handleChange}/>
					<input type='submit' name='verify' id='verify' value='Verify'/>
				</form>

				<br/><br/>
				<a href='/'>Goto Back</a>

			</div>
		)
	}


}

export default EmailOTP
