import React from 'react';
import { getAuth, RecaptchaVerifier } from "firebase/auth";

import './App.css'


class App extends React.Component {

	handleChange = (e) => {
		const { name, value } = e.target
		this.setState({
			[name]: value
		})
	}

	configureCaptcha = () =>{
		const auth = getAuth();

		window.recaptchaVerifier = new RecaptchaVerifier(auth, 'sign-in-button', {
		'size': 'invisible',
		'callback': (response) => {
			// reCAPTCHA solved, allow signInWithPhoneNumber.
			onSignInSubmit();
		}
		});
	}


	render() {
		return(
			<div className='app'>

				<h2>Verify Your Mobile Number</h2>

				<h4>Enter Number</h4>
				<form>
					<input type="tel" name='mobile' id='mobile' placeholder="Enter your mobile number" required  onChange={this.handleChange}/>

					<input type='submit' name='get_code' id='get_code' value='Get Code'/>
				</form>

				<h4>Enter OTP</h4>
				<form>
					<input type="number" name='otp' id='otp' placeholder="Enter your OTP number" required onChange={this.handleChange}/>

					<input type='submit' name='verify' id='verify' value='Verify'/>
				</form>
			</div>
		)
	}


}

export default App
