import React, { useCallback } from 'react';

import emailjs from '@emailjs/browser';

import './App.css'


class EmailOTP extends React.Component {

	handleChange = (e) => {
		const { name, value } = e.target
		this.setState({
			[name]: value
		})
	}

	onSubmitEmail = (e) => {

		const params = {
			from_name: 'Verification',
			otp: 100100
		};

		emailjs.send('service_7mfhixv','template_i6cppcw', params, 'btVGQG6XQEU4PFvoX')
			.then((response) => {
			console.log('SUCCESS!', response.status, response.text);
			}, (err) => {
			console.log('FAILED...', err);
			});
	}
	render() {
		return(
			<div className='EmailOTP body'>

				<h2>Verify Your Email</h2>

				<h4>Enter E-Mail</h4>
				<form onSubmit={this.onSubmitEmail}>
					<input type="email" name='email' id='email' placeholder="Enter your Email" required  onChange={this.handleChange}/>
					<input type='submit' name='sign-in-button' id='sign-in-button' value='Get Code'/>
				</form>

				<h4>Enter OTP</h4>
				<form onSubmit={this.onSubmitOTP}>
					<input type="number" name='otp' id='otp' placeholder="Enter your OTP number" required onChange={this.handleChange}/>
					<input type='submit' name='verify' id='verify' value='Verify'/>
				</form>

				<br/><br/>
				<a href='/'>Goto Back</a>

			</div>
		)
	}


}

export default EmailOTP
