	const [form, setForm] = useState({});

    const handleForm = (e) => {
        setForm({
            ...form,
            [e.target.name] : e.target.value
        })
    }
    const handleSubmit = async (e) => {
        e.preventDefault()
        const response = await fetch('http://localhost:8000/',{
            method: 'POST',
            body: JSON.stringify(form),
            headers:{
                'Content-Type': 'application/json'
            }
        });
        const data = await response.json();
        console.log(response);
        console.log(data)
    }

	return(
		<div className='EmailOTP body'>

			<h2>Verify Your Email</h2>

			<h4>Enter E-Mail</h4>
			<form onSubmit={this.onSubmitEmail}>
				<input type="email" name='email' id='email' placeholder="Enter your Email" required  onChange={handleChange}/>
				<input type='submit' name='sign-in-button' id='sign-in-button' value='Get Code'/>
			</form>

			<h4>Enter OTP</h4>
			<form onSubmit={this.onSubmitOTP}>
				<input type="number" name='otp' id='otp' placeholder="Enter your OTP number" required onChange={handleChange}/>
