import React from 'react';
import './firebase';
import { getAuth, RecaptchaVerifier, signInWithPhoneNumber } from "firebase/auth";

import './App.css'


class MobileOTP extends React.Component {

	handleChange = (e) => {
		const { name, value } = e.target
		this.setState({
			[name]: value
		})
	}

	validation = (e) => {

		const errorMsg = document.getElementById('validate-err')
		console.log(errorMsg);
		console.log(this.state.mobile.length)
		if (this.state.mobile.length <= 10)
		{
			e.preventDefault();
			alert(errorMsg+' failed');
			errorMsg.innerText = 'Enter Mobile Number';
			return false;
		}
	}

	configureCaptcha = () =>{
		const auth = getAuth();

		window.recaptchaVerifier = new RecaptchaVerifier(auth, 'sign-in-button', {
		'size': 'invisible',
		'callback': (response) => {
			// reCAPTCHA solved, allow signInWithPhoneNumber.
			this.onSignInSubmit();
			console.log("ReCaptcha Verified")
		},
		defaultCountry : 'IN'
		});
	}

	onSignInSubmit = (e) => {
		e.preventDefault();
		validation(e);
		this.configureCaptcha();
		const auth = getAuth();

		const phoneNumber = "+91" + this.state.mobile;
		console.log(phoneNumber);
		const appVerifier = window.recaptchaVerifier;
		signInWithPhoneNumber(auth, phoneNumber, appVerifier)
			.then((confirmationResult) => {
			// SMS sent. Prompt user to type the code from the message, then sign the
			// user in with confirmationResult.confirm(code).
			window.confirmationResult = confirmationResult;
			alert("Code is Sent");
			// ...
			}).catch((error) => {
			// Error; SMS not sent
			// ...
			console.log(error);
			console.log("Error in Sending OTP message")
			});
	}

	onSubmitOTP = (e) => {
		e.preventDefault();

		const code = this.state.otp;
		console.log(code);
		window.confirmationResult.confirm(code).then((result) => {
		// User signed in successfully.
		const user = result.user;
		console.log(JSON.stringify(user))
		alert("User signed in successfully");
		// ...
		}).catch((error) => {
		// User couldn't sign in (bad verification code?)
		// ...
		console.log(error);
		console.log("Error in veryfying OTP")
		});
	}

	render() {
		return(
			<div className='MobileOTP body'>

				<h2>Verify Your Mobile Number</h2>

				<h4>Enter Number</h4>
				<form onSubmit={this.onSignInSubmit}>
					<input type="tel" name='mobile' id='mobile' placeholder="Enter your mobile number" onChange={this.handleChange}/>
					<div id="validate-err">f</div>
					<input type='submit' name='sign-in-button' id='sign-in-button' value='Get Code'/>
				</form>

				<h4>Enter OTP</h4>
				<form onSubmit={this.onSubmitOTP}>
					<input type="number" name='otp' id='otp' placeholder="Enter your OTP number" required onChange={this.handleChange}/>
					<input type='submit' name='verify' id='verify' value='Verify'/>
				</form>

				<br/><br/>
				<a href='/'>Goto Back</a>

			</div>
		)
	}


}

export default MobileOTP
