		console.log(otpNumber);

		var params = {
			mail_to : email.value,
			otp : otpNumber,
		};

		emailjs.send('service_7mfhixv', 'template_i6cppcw', params, 'btVGQG6XQEU4PFvoX')
			.then(function(response) {
			   console.log('SUCCESS!', response.status, response.text);
			   alert('Email sent!');
			}, function(error) {
			   console.log('FAILED...', error);
